import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import blogActions from "../redux/actions/blog.actions";
import { useParams } from "react-router-dom";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import Grid from "@material-ui/core/Grid";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";
import { red } from "@material-ui/core/colors";
import Moment from "react-moment";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 0,
    paddingTop: "56.25%", // 16:9
  },

  avatar: {
    backgroundColor: red[500],
  },
}));

function BlogDetailPage() {
  const classes = useStyles();
  const { blogId } = useParams();
  const blogDetail = useSelector((state) => state.blog.selectedBlog);
  const loading = useSelector((state) => state.blog.loadingSelectedBlog);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(blogActions.getSingleBlog(blogId));
  }, [dispatch]);

  if (!loading && blogDetail.images !== null) {
    return (
      <Grid item xs={9}>
        <Card style={{ width: "100%", height: "100%" }}>
          <CardHeader
            avatar={
              <Avatar
                aria-label="avatar"
                className={classes.avatar}
                src={blogDetail.author.avatarUrl}
              ></Avatar>
            }
            title={blogDetail.author.name}
          />
          <img src={blogDetail.images[0]} alt="" />
          <CardMedia image={blogDetail.images[0]} title={blogDetail.title} />
          <CardContent>
            <Typography variant="body2" color="textSecondary" component="p">
              {blogDetail.content.length <= 100
                ? blogDetail.content
                : blogDetail.content.slice(0, 100) + "..."}
              <br /> {<Moment fromNow>{blogDetail.createdAt}</Moment>}
            </Typography>
          </CardContent>
        </Card>
      </Grid>
    );
  }
  return <div>hellloo</div>;
}

export default BlogDetailPage;
